function disableButton(btnId) {
  document.getElementById(btnId).disabled = 'true';
  return true;
}

function checkTakenEmail()  {
    $.ajax(
        {
            url      : '/subscriber/checkEmail',
            type     : 'get',
            dataType : 'json',
            data     :
            {
                email : $('#email1').val()
            }
        }).done(function (response)
        {
            if (response.unique) {
                $('#takenMessage').hide();
            } else {
                $('#takenMessage').show();
            }

        });
}

function checkConfirmEmail() {
    if ($('#email1').val() === $('#email2').val()) {
        $('#confirmMessage').hide();
    } else {

        $('#confirmMessage').show();
    }
}
function checkConfirmPassword() {
    if ($('#password1').val() === $('#password2').val()) {
        $('#passwordMessage').hide();
    } else {
        $('#passwordMessage').show();
    }
}

function toggleList() {
    $('.search-result').removeClass('grid');
    $('.search-result').addClass('list');
    $('.grid-list-switch .fa-list').addClass('active');
    $('.fa-th').removeClass('active');
}

function toggleGrid() {
    $('.search-result').removeClass('list');
    $('.search-result').addClass('grid');
    $('.grid-list-switch .fa-th').addClass('active');
    $('.fa-list').removeClass('active');
}

